import React, { useState, useEffect } from 'react';
import Product from './Product';
import "./Home.css";
import db from '../firebase';
function Home({ search }) {
    const [products, setProducts] = useState([]);
    useEffect(() => {
        console.log(search);
        db.collection('products').onSnapshot(snapshot => {
            setProducts(snapshot.docs.map(doc => ({
                id: doc.id, Retailer: doc.data().retailerName,
                Product_details: doc.data().productDescription
                , deal_price: doc.data().dealPrice
                , product_link: doc.data().productUrl
                , mrp: doc.data().mrp
                , off: doc.data().discount
                , img: doc.data().productImage
            })))
        })
    }, []);

    return (
        <div className="home">
            <div className="home__row">
                {products.map(product => (
                    <Product Retailer={product.Retailer} Product_details={product.Product_details}
                        deal_price={product.deal_price}
                        product_link={product.product_link}
                        mrp={product.mrp}
                        off={product.off}
                        img={product.img} />
                ))}
            </div>
        </div>
    )
}

export default Home
